      <PropsCompo1 title="속성컴포넌트1" content="속성을 갖는 컴포넌트" />PropsCompo1
      <h4>3. 태그내용을 갖는 컴포넌트</h4>
      <h4>4. 상태값을 갖는 컴포넌트</h4>
    </div>
  );
}

export default App;
