import React, { useState } from 'react';

const EventFunctionCompo = () => {
    // 상태값 선언
    const [color, setColor] = useState("#000");
    const [fcolor, setFcolor] = useState("#000");
    const [msg, setMsg] = useState("내용입력하세요");

    // 이벤트 핸들러 정의
    // 기본 자바스크립트 함수
    function onClickHandler1(){
        alert("핸들러 버튼1 클릭")
    }
    
    // 자바스크립트 익명함수
    const onClickHandler2 = function(){
        alert("핸들러 버튼 2 클릭")
    }
    
    // 타입스크립트 화살표 함수
    const onClickHandler3 = ()=>{
        alert("핸들러 버튼 3 클릭")
    };

    return (
        <div className="EventFunctionCompo">
            <em>EventFunctionCompo</em>
            {/*onClick 이벤트*/}
            <h1>React Click Event</h1>
            <button onClick={()=>{alert("기본클릭")}}>기본클릭</button>
            <button onClick={onClickHandler1}>핸들러 버튼1</button>
            <button onClick={onClickHandler2}>핸들러 버튼2</button>
            <button onClick={onClickHandler3}>핸들러 버튼3</button>
            <button onClick={(e)=>{console.log(e)}}>이벤트 객체</button>
            <button onClick={()=>{setColor("#f00")}}>색상 변경</button>
            <button onClick={()=>{}}>색상 토글</button>
            {/*onMouseOver, onMouseHover 이벤트*/}
            {/*onChange 이벤트*/}
        </div>
    );
};

export default EventFunctionCompo;
