import React from "react";
import logo from "./logo.svg";
import "./App.css";
/*
  날짜 : 2020/10/21
  이름 : 이태훈
  내용 : 컴포넌트 생명주기 실습하기

  컴포넌트 생명주기
    - 리액트의 모든 컴포넌트는 초기화(mount)단계, 업데이트(update)단계, 소멸(unmount)단계를 거친다.
    - 각 단계 별로 자동으로 실행되는 함수를 생명주기 함수라고 한다.
    - 생명주기 함수는 클래스 컴포넌트에서만 동작한다.
*/

function App() {
  return (
    <div>
      <h3>ch07. 리액트 컴포넌트 생명주기</h3>
    </div>
  );
}

export default App;
