import React, { useState } from "react";

const UseStateCompo = () => {
  // 상태값 생성
  const [state, setState] = useState({ name: "", age: 0 });
  return (
    <div className="UseStateCompo">
      <h4>UseStateCompo</h4>
      <p>
        이름 : {state.name}
        <br />
        나이 : {state.age}
      </p>
    </div>
  );
};

export default UseStateCompo;
