import React from "react";
import logo from "./logo.svg";
import "./App.css";
import { Link, Route } from "react-router-dom";
import Home from "./components/Home";
import About from "./components/About";
import QueryCompo from "./components/QueryCompo";
/*
  날짜 : 2020/10/22
  이름 : 이태훈
  내용 : 리액트 라우터(Router)

  리액트 라우터
    - SPA(Single Page Application) 개발하기 위한 화면 이도을 지원하는 리액트 라이브러리
*/

function App() {
  return (
    <div>
      <h3>ch09. 리액트 Router</h3>
      <ul>
        <li>
          <Link to="/home">Home</Link>
        </li>
        <li>
          <Link to="/about">About</Link>
        </li>
        <li>
          <Link to="/query?name=김유신">QueryCompo</Link>
        </li>
        <li>
          <Link to="/query?name=김유신&age=25">QueryCompo</Link>
        </li>
        <li>
          <Link to="/query?name=김유신&age=25&addr=부산">QueryCompo</Link>
        </li>
      </ul>
      {/* Router Path 설정 */}
      <Route path="/" component={Home} exact={true} />
      <Route path="/home" component={Home} />
      <Route path="/about" component={About} />

      <Route path="/query" component={QueryCompo} />
    </div>
  );
}

export default App;
